mapper.select:
SELECT B.* FROM( SELECT A.*, ROWNUM RNUM FROM( SELECT MEM_ID, MEM_NAME , MEM_BIR , MEM_HP, MEM_MAIL, MEM_ADD1 , MEM_MEMORIALDAY , MEM_MILEAGE , ( SELECT COUNT(DISTINCT CART_PROD) FROM CART WHERE CART_MEMBER = MEM_ID ) PROD_COUNT
ORDER BY ROWID DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
